<?php
declare(strict_types = 1);

namespace spec\Advent\Day2;

use Advent\Day2\PasscodeDecoder;
use PhpSpec\ObjectBehavior;
use Prophecy\Argument;

class PasscodeDecoderSpec extends ObjectBehavior
{
    function let()
    {
        $this->beConstructedWith('U');
    }

    function it_is_initializable()
    {
        $this->shouldHaveType(PasscodeDecoder::class);
    }

    function it_can_decode_bathroom_instructions()
    {
        $this->beConstructedWith('ULL
RRDDD
LURDL
UUUUD');

        $this->getBathroomCode()->shouldBe('1985');
    }

    function it_can_solve_part_1()
    {
        $this->beConstructedWith('



');

        $this->getBathroomCode()->shouldBe('95549');
    }

    function it_can_solve_the_exmaple_for_the_executive_keypad()
    {
        $this->beConstructedWith('ULL
RRDDD
LURDL
UUUUD');

        $this->getExecutiveBathroomCode()->shouldBe('5DB3');
    }

    function it_can_solve_the_real_code_on_the_executive_bathroom()
    {
        $this->beConstructedWith('



');

        $this->getExecutiveBathroomCode()->shouldBe('D87AD');
    }
}
